import React, { Component } from "react";
import { Map, HeatMap, GoogleApiWrapper } from "google-maps-react";

const mapStyles = {
  width: '45%',
  height: '54%'
};

const gradient = [
  "rgba(0, 255, 255, 0)",
  "rgba(0, 255, 255, 1)",
  "rgba(0, 191, 255, 1)",
  "rgba(0, 127, 255, 1)",
  "rgba(0, 63, 255, 1)",
  "rgba(0, 0, 255, 1)",
  "rgba(0, 0, 223, 1)",
  "rgba(0, 0, 191, 1)",
  "rgba(0, 0, 159, 1)",
  "rgba(0, 0, 127, 1)",
  "rgba(63, 0, 91, 1)",
  "rgba(127, 0, 63, 1)",
  "rgba(191, 0, 31, 1)",
  "rgba(255, 0, 0, 1)"
];

class MapContainer extends React.Component {
  constructor(){
    this.state = {
      isHeatVisible : true
    };
    this.handleMapMount = this.handleMapMount.bind(this);
  }
  toggleHeatmap = () => {
    this.setState({isHeatVisible: !this.state.isHeatVisible});
  }
  handleMapMount(mapProps, map) {
    this.map = map;

    //log map bounds
    console.log(this.map.getBounds());
  }
  render() {
    {this.state.isHeatVisible ? HeatMap : null}
    let heat = <HeatMap
            //gradient={gradient}
            positions={this.props.positions}
            opacity={1}
            radius={20}
          />
    return (
      <div className="map-container" >
        <Map
          style={mapStyles}
          google={this.props.google}
          className={"map"}
          zoom={this.props.zoom}
          initialCenter={this.props.center}
          onReady={this.handleMapMount}
        >
          <div id="floating-panel">
            <button id="toggleHeatmap"onClick={this.toggleHeatmap}>Toggle HeatMap</button>
          </div>
          {this.state.isHeatVisible ? heat: null}
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyAmJ92b4CXEobypIq9cuEEcl9CMpZ8HCe8",
  language: "th",
  libraries: ["visualization"]
})(MapContainer);
